import React, { Component } from 'react'
import MainContainer1 from './mainContainer1'
import MainContainer2 from './mainComponent2'
import MainContainer3 from './mainContainer3'
// import './actionComponentCSS.css'
import './postButtonCSS.css'

class ActionComponent extends Component {

    constructor(props) {
        super(props)

        this.state = {
            comp1: true,
            comp2: false,
            comp3: false
        }
    }

    handleEventPage1 = () => {
        this.setState(prevState => ({
            comp1: !prevState.comp1,
            comp2: !prevState.comp2
        }))
    }

    handleEventPage2Btn1 = () => {
        this.setState(prevState => ({
            comp1: !prevState.comp1,
            comp2: !prevState.comp2
        }))
    }

    handleEventPage2Btn2 = () => {
        this.setState(prevState => ({
            comp2: !prevState.comp2,
            comp3: !prevState.comp3
        }))
    }

    handleEventPage3Btn1 = () => {
        this.setState(prevState => ({
            comp3: !prevState.comp3,
            comp2: !prevState.comp2
        }))
    }

    handleEventPage3Btn2 = () => {
        this.setState(prevState => ({
            comp3: !prevState.comp3,
            comp2: !prevState.comp3
        }))
    }

    render() {
        return (
            <div>
                <div >
                    {
                        this.state.comp1 &&
                        <div>
                            <MainContainer1 >
                                <button onClick={this.handleEventPage1} className='buttonStyle'>OLDER POSTS</button>

                            </MainContainer1>

                        </div>
                    }

                </div>

                <div>
                    {
                        this.state.comp2 &&
                        <div>
                            <MainContainer2 >
                                <div >
                                    <button onClick={this.handleEventPage2Btn1} className='buttonStyle' id='btnPage2' >NEWER POSTS</button>
                                    <button onClick={this.handleEventPage2Btn2} className='buttonStyle'>OLDER POSTS</button>
                                </div>
                            </MainContainer2>

                    </div>
                }

                </div>
                <div>
                    {
                        this.state.comp3 &&
                        <div>
                            <MainContainer3 >
                                <div >
                                    <button onClick={this.handleEventPage3Btn1} className='buttonStyle' id='btnPage2' >NEWER POSTS</button>
                                    <button onClick={this.handleEventPage3Btn2} className='buttonStyle'>OLDER POSTS</button>
                                </div>
                            </MainContainer3>

                    </div>
                }

                </div>
                </div>
        )
    }
}

export default ActionComponent
