import React, { Component } from 'react'
import MainContainer1 from './mainContainer1'
import MainContainer2 from './mainComponent2'
// import './actionComponentCSS.css'
import './postButtonCSS.css'

class ActionComponent extends Component {

    constructor(props) {
        super(props)

        this.state = {
             comp1: true,
             comp2: false,
             comp3: false
        }
    }

    handleEvent = () => {
        this.setState(prevState => ({
            comp1: !prevState.comp1,
            comp2: !prevState.comp2
        }))
    }

    render() {
        return (
            <div>
                <div >
                {
                    this.state.comp1 && 
                    <div>
                     <MainContainer1 >
                     <button onClick={this.handleEvent} className='buttonStyle'>OLDER POSTS</button>

                     </MainContainer1>

                    </div>
                }

                </div>

                <div>
                {
                    this.state.comp2 && 
                    <div> <MainContainer2 />

                    </div>
                }

                </div>
                </div>
        )
    }
}

export default ActionComponent
